
import { useState } from 'react';
import './App.css'
import { ProductList } from './componets/productList';
import Basket from './componets/basket';


function App() {
  const [basket , setBasket] = useState([])
  const [products, setProducts] = useState([
    {
      id: 1,
      title: "Wireless Headphones",
      price: 49.99,
      category: "Electronics",
      photo: "data:image/jpeg;base64,",
    },
    {
      id: 2,
      title: "Yoga Mat",
      price: 19.99,
      category: "Fitness",
      photo: "data:image/jpeg;base64,",
    },
    {
      id: 3,
      title: "Desk Lamp",
      price: 29.99,
      category: "Home Decor",
      photo: "data:image/jpeg;base64,",
    },
    {
      id: 4,
      title: "Running Shoes",
      price: 59.99,
      category: "Footwear",
      photo: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSeHBmJxVWb4uQZDyIfW102jxgGn-v7MT1RrQ&s",
    },
    {
      id: 5,
      title: "Bluetooth Speaker",
      price: 39.99,
      category: "Electronics",
      photo: "data:image/jpeg;base64,",
    },
    {
      id: 6,
      title: "Cooking Utensil Set",
      price: 24.99,
      category: "Kitchen",
      photo: "data:image/jpeg;base64,",
    },
    {
      id: 7,
      title: "Notebook",
      price: 4.99,
      category: "Stationery",
      photo: "data:image/jpeg;base64,",
    },
    {
      id: 8,
      title: "Gaming Mouse",
      price: 34.99,
      category: "Gaming",
      photo: "data:image/jpeg;base64,",
    },
    {
      id: 9,
      title: "Sunglasses",
      price: 14.99,
      category: "Accessories",
      photo: "data:image/jpeg;base64,",
    },
    {
      id: 10,
      title: "Portable Charger",
      price: 25.99,
      category: "Electronics",
      photo: "data:image/jpeg;base64,",
    },
  ]);


  const moveToCart = id => {
    const found = products.find(product => product.id === id);


    const productInBasket = basket.find(item => item.id === id);

    if (productInBasket) {

      setBasket(prev =>
        prev.map(item =>
          item.id === id ? { ...item, count: item.count + 1 } : item
        )
      );
    } else {
  
      setBasket([...basket, { ...found, count: 1 }]);
    }
  };

  const increaseCount = id => {
    setBasket(prev =>
      prev.map(item =>
        item.id === id ? { ...item, count: item.count + 1 } : item
      )
    );
  };


  const decreaseCount = id => {
    setBasket(prev =>
      prev.map(item =>
        item.id === id && item.count > 1
          ? { ...item, count: item.count - 1 }
          : item
      )
    );
  };

  
  const removeFromCart = id => {
    setBasket(prev => prev.filter(item => item.id !== id));
  };

  return (
    <>
      <h1>Online Shop</h1>
      <ProductList iteme={products} onMove={moveToCart} />
      <Basket
        cart={basket}
        onIncrease={increaseCount}
        onDecrease={decreaseCount}
        onRemove={removeFromCart}
      />
    </>
  );
}

export default App;
